mapper.select:
select
	*
from
	d_datasource_database
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and datasource_id = #{datasourceId} 


		and database_id = #{databaseId} 


		and is_default = #{isDefault} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	d_datasource_database
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and datasource_id = #{datasourceId} 


		and database_id = #{databaseId} 


		and is_default = #{isDefault} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
