mapper.select:
select
	*
from
	fs_media
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and category_id = #{categoryId} 


		and type = #{type} 


		and user_id = #{userId} 


		and name = #{name} 


		and realname = #{realname} 


		and uri = #{uri} 


		and note = #{note} 


		and feature = #{feature} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	fs_media
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and category_id = #{categoryId} 


		and type = #{type} 


		and user_id = #{userId} 


		and name = #{name} 


		and realname = #{realname} 


		and uri = #{uri} 


		and note = #{note} 


		and feature = #{feature}
