mapper.select:
select
	*
from
	item_spu_template
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and category_id = #{categoryId} 


		and property_id = #{propertyId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	item_spu_template
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and category_id = #{categoryId} 


		and property_id = #{propertyId}
