mapper.select:
select
	*
from
	item_spu_template_value
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and spu_template_id = #{spuTemplateId} 


		and value_id = #{valueId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	item_spu_template_value
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and spu_template_id = #{spuTemplateId} 


		and value_id = #{valueId}
