mapper.delete:
update lang_content set is_deleted = 1 , gmt_modified = now() where id in
#{primary}
and is_deleted = 0

mapper.select:
select
	*
from
	lang_content
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and lang_id = #{langId} 


		and category = #{category} 


		and content = #{content} 


		and gmt_modifier = #{gmtModifier} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	lang_content
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and lang_id = #{langId} 


		and category = #{category} 


		and content = #{content} 


		and gmt_modifier = #{gmtModifier} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
