mapper.delete:
update a_api set is_deleted = 1 , gmt_modified = now() where id in
#{primary}
and is_deleted = 0

mapper.select:
select
	*
from
	a_api
where 
	is_deleted = 0

	    and id = #{id} 


		and name = #{name} 


		and title = #{title} 


		and pid = #{pid} 


		and app_id = #{appId} 


		and type = #{type} 


		and uri = #{uri} 


		and form_method = #{formMethod} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	a_api
where
	is_deleted = 0

	    and id = #{id} 


		and name = #{name} 


		and title = #{title} 


		and pid = #{pid} 


		and app_id = #{appId} 


		and type = #{type} 


		and uri = #{uri} 


		and form_method = #{formMethod} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
