mapper.select:
select
	*
from
	a_api_mock
where 
	is_deleted = 0

	    and id = #{id} 


		and api_id = #{apiId} 


		and name = #{name} 


		and req = #{req} 


		and rsp = #{rsp} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	a_api_mock
where
	is_deleted = 0

	    and id = #{id} 


		and api_id = #{apiId} 


		and name = #{name} 


		and req = #{req} 


		and rsp = #{rsp} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
