mapper.select:
		select
			*
		from
            ik_bids
		where 
			is_deleted = 0

			    and related_id = #{relatedId} 


			    and type = #{type} 


			    and user_id = #{userId} 


			    and name = #{name} 


			    and status = #{status} 


			    and img_ids = #{imgIds} 


			    and content = #{content} 


			    and feature = #{feature} 


			    and view_count = #{viewCount} 


			    and attention_count = #{attentionCount} 


			    and id = #{id} 


			    and in_process_start_time = #{inProcessStartTime} 


			    and in_process_end_time = #{inProcessEndTime} 


			    and pre_heat_start_time = #{preHeatStartTime} 


			    and pre_heat_end_time = #{preHeatEndTime} 


			    and result_id = #{resultId} 


			    and result_time = #{resultTime} 


			    and str1 = #{str1} 


			    and str2 = #{str2} 


			    and str3 = #{str3} 


			    and str4 = #{str4} 


			    and str5 = #{str5} 


			    and str6 = #{str6} 


