			    and dou2 = #{dou2} 


			    and dou3 = #{dou3} 


			    and integer1 = #{integer1} 


			    and integer2 = #{integer2} 


			    and integer3 = #{integer3} 


			    and status in 

		            	#{statusTmp}



		    order by 
				${query.orderBy} 

					${query.order} 



		   	limit 
		   		#{_offset} , #{_pageSize}

		select
			count(id)
		from
            ik_bids
		where
			is_deleted = 0

			    and related_id = #{relatedId} 


			    and type = #{type} 


			    and user_id = #{userId} 


			    and name = #{name} 


			    and status = #{status} 


			    and content = #{content} 


			    and img_ids = #{imgIds} 


