mapper.select:
select
	*
from
	ik_paticipate
where 
	is_deleted = 0

	    and id = #{id} 


	    and type = #{type} 


	    and user_id = #{userId} 


	    and related_id = #{relatedId} 


	    and feature = #{feature} 


	    and gmt_created > #{query.startGmtCreated} 


	    and gmt_created < #{query.endGmtCreated} 


    order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	ik_paticipate
where
	is_deleted = 0

	    and id = #{id} 


	    and type = #{type} 


	    and user_id = #{userId} 


	    and feature = #{feature} 


	    and related_id = #{relatedId} 


	    and gmt_created > #{query.startGmtCreated} 


	    and gmt_created < #{query.endGmtCreated}
