mapper.select:
select
	*
from
	cms_page_category_related
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and type = #{type} 


		and category_id = #{categoryId} 


		and page_id = #{pageId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	cms_page_category_related
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and type = #{type} 


		and category_id = #{categoryId} 


		and page_id = #{pageId}
