mapper.select:
select
	*
from
	shop_complaint
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and type = #{type} 


		and shop_id = #{shopId} 


		and item_id = #{itemId} 


		and user_id = #{userId} 


		and status = #{status} 


		and title = #{title} 


		and content = #{content} 


		and feature = #{feature} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	shop_complaint
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and type = #{type} 


		and shop_id = #{shopId} 


		and item_id = #{itemId} 


		and user_id = #{userId} 


		and status = #{status} 


		and title = #{title} 


		and content = #{content} 


		and feature = #{feature}
