mapper.select:
select
	*
from
	item_compare
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and user_id = #{userId} 


		and item_id = #{itemId} 


		and gmt_created  > #{query.startTime}


		and gmt_created < #{query.endTime}


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	item_compare
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and user_id = #{userId} 


		and item_id = #{itemId} 


		and gmt_created  > #{query.startTime}


		and gmt_created < #{query.endTime}
