mapper.delete:
update item_flash_activity_time set is_deleted = 1 , gmt_modified = now() where id in
#{primary}
and is_deleted = 0

mapper.select:
select
	*
from
	item_flash_activity_time
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and shop_id = #{shopId} 


		and activity_id = #{activityId} 


		and name = #{name} 


		and status = #{status} 


		and start_time = #{startTime} 


		and end_time = #{endTime} 


		and item_count = #{itemCount} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	item_flash_activity_time
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and shop_id = #{shopId} 


		and activity_id = #{activityId} 


		and name = #{name} 


		and status = #{status} 


		and start_time = #{startTime} 


		and end_time = #{endTime} 


		and item_count = #{itemCount} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
