mapper.delete:
update item_popular set is_deleted = 1 , gmt_modified = now() where id in
#{primary}
and is_deleted = 0

mapper.select:
select
	*
from
	item_popular
where 
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and shop_id = #{shopId} 


		and item_id = #{itemId} 


		and name = #{name} 


		and status = #{status} 


		and note = #{note} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId} 


	order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(1)
from
	item_popular
where
	is_deleted = 0

	    and id = #{id} 


		and saas_id = #{saasId} 


		and shop_id = #{shopId} 


		and item_id = #{itemId} 


		and name = #{name} 


		and status = #{status} 


		and note = #{note} 


		and sort = #{sort} 


		and feature = #{feature} 


		and id >= #{query.minId} 


		and id <= #{query.maxId}
