mapper.select:
select
	*
from
	ik_like
where 
	is_deleted = 0

	    and id = #{id} 


	    and type = #{type} 


	    and user_id = #{userId} 


	    and related_id = #{relatedId} 


    order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	ik_like
where
	is_deleted = 0

	    and id = #{id} 


	    and type = #{type} 


	    and user_id = #{userId} 


	    and related_id = #{relatedId}
