mapper.select:
select
	*
from
	u_role_apply
where 
	is_deleted = 0

	    and id = #{id} 


	    and user_id = #{userId} 


	    and role_id = #{roleId} 


	    and status = #{status} 


	    and feature = #{feature} 


    order by 
		${query.orderBy} 

			${query.order} 



   	limit 
   		#{_offset} , #{_pageSize}

select
	count(id)
from
	u_role_apply
where
	is_deleted = 0

	    and id = #{id} 


	    and user_id = #{userId} 


	    and role_id = #{roleId} 


	    and status = #{status} 


	    and feature = #{feature}
